const btnSlider=document.querySelectorAll(".button__slider"),sliderControls=document.querySelector(".slider-controls"),sliderItem=document.querySelectorAll(".slider__item");sliderControls.addEventListener("click",e=>{let t=e.target;if(t.classList.contains("button__slider")){for(let e=0;e<btnSlider.length;e++)btnSlider[e].classList.remove("current"),sliderItem[e].classList.remove("slide-current");t.classList.add("current");for(let e=0;e<btnSlider.length;e++)btnSlider[e].classList.contains("current")&&sliderItem[e].classList.add("slide-current")}});
